mapper.select:
SELECT orderSub.order_sub_no AS orderSubNo, mo.`pay_type` AS payType, orderSub.sub_flag AS subPayStatus, mo.`resource` AS resource, mo.`create_time`, mo.order_no as orderNo, mo.`name`, mo.`mobile`, mo.`resource` FROM m_order mo ,m_logistics logistics, m_order_sub orderSub WHERE orderSub.`order_no` = mo.`order_no` AND orderSub.`order_no` = logistics.`order_no`
AND orderSub.sub_flag = #{record.payStatus}
AND mo.mobile = #{record.mobile}
AND orderSub.order_sub_no = #{record.orderSubNo}
AND mo.resource = #{record.resource}
AND orderSub.channel_id = #{record.channelId,jdbcType=INTEGER}
AND mo.create_time >= FROM_UNIXTIME(#{record.bookingStartDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 00:00:00')
AND mo.create_time <= FROM_UNIXTIME(#{record.bookingEndDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 23:59:59')
and orderSub.sub_flag in
#{item}
AND mo.name like CONCAT('%',#{record.name},'%')
AND logistics.consignee_name like CONCAT('%',#{record.receiver},'%')
AND logistics.consignee_mobile like CONCAT('%',#{record.receiverMobile},'%')
ORDER BY create_time desc
LIMIT #{record.pageNo,jdbcType=INTEGER} ,#{record.pageNumber,jdbcType=INTEGER}

SELECT count(orderSub.order_sub_no) FROM m_order mo ,m_logistics logistics, m_order_sub orderSub WHERE orderSub.`order_no` = mo.`order_no` AND orderSub.`order_no` = logistics.`order_no`
AND orderSub.sub_flag = #{record.payStatus}
AND mo.mobile = #{record.mobile}
AND orderSub.order_sub_no = #{record.orderSubNo}
AND mo.resource = #{record.resource}
AND orderSub.channel_id = #{record.channelId,jdbcType=INTEGER}
AND mo.create_time >= FROM_UNIXTIME(#{record.bookingStartDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 00:00:00')
AND mo.create_time <= FROM_UNIXTIME(#{record.bookingEndDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 23:59:59')
and orderSub.sub_flag in
#{item}
AND mo.name like CONCAT('%',#{record.name},'%')
AND logistics.consignee_name like CONCAT('%',#{record.receiver},'%')
AND logistics.consignee_mobile like CONCAT('%',#{record.receiverMobile},'%')
ORDER BY create_time desc
LIMIT #{record.pageNo,jdbcType=INTEGER} ,#{record.pageNumber,jdbcType=INTEGER}
