mapper.select:
SELECT DISTINCT(product.`order_sub_no`) AS orderSubNo, mo.`pay_type` AS payType, mo.`pay_status` AS subPayStatus, mo.`resource` AS resource, mo.`create_time`, logistics.`consignee_name`, logistics.`consignee_mobile`, mo.order_no as orderNo, mo.`name`, mo.`mobile`, mo.`resource` FROM m_order_product product, m_order mo ,m_logistics logistics WHERE product.`order_no` = mo.`order_no` AND product.`order_no` = logistics.`order_no`
AND mo.pay_status = #{record.payStatus}
AND mo.mobile = #{record.mobile}
AND product.order_sub_no = #{record.orderSubNo}
AND mo.resource = #{record.resource}
AND product.chanelid = #{record.channelId,jdbcType=INTEGER}
AND mo.create_time <= FROM_UNIXTIME(#{record.bookingEndDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 23:59:59')
and mo.pay_status in
#{item}
AND product.order_no in (select order_no from m_order where name like CONCAT('%',#{record.name},'%'))
AND product.order_no in (select order_no from m_logistics where consignee_name like CONCAT('%',#{record.receiver},'%'))
AND product.order_no in (select order_no from m_logistics where consignee_mobile = #{record.receiverMobile})
ORDER BY create_time desc
LIMIT #{record.pageNo,jdbcType=INTEGER} ,#{record.pageNumber,jdbcType=INTEGER}

SELECT count(DISTINCT(product.`order_sub_no`)) FROM m_order_product product, m_order mo ,m_logistics logistics WHERE product.`order_no` = mo.`order_no` AND product.`order_no` = logistics.`order_no`
AND mo.pay_status = #{record.payStatus}
AND mo.mobile = #{record.mobile}
AND product.order_sub_no = #{record.orderSubNo}
AND mo.resource = #{record.resource}
AND product.chanelid = #{record.channelId,jdbcType=INTEGER}
AND mo.create_time <= FROM_UNIXTIME(#{record.bookingEndDate,jdbcType=TIMESTAMP}/1000, '%Y-%m-%d 23:59:59')
and mo.pay_status in
#{item}
AND product.order_no in (select order_no from m_order where name like CONCAT('%',#{record.name},'%'))
AND product.order_no in (select order_no from m_logistics where consignee_name like CONCAT('%',#{record.receiver},'%'))
AND product.order_no in (select order_no from m_logistics where consignee_mobile = #{record.receiverMobile})
